import Bubble from "../Bubble";
function NewFeatures({ data }) {
  if (!data) return null;
  return (
    <div className="bubbleInner">
      <Bubble position="top-right rotate-180" />
      <div className="box center contentBoxs">
        <div className="header">
          <span className="label"> New Features</span>
          <h2>
            Enhance Your Creativity with <br />
            <span> AI-Powered Tools</span>
          </h2>
          <p>
            Our app offers powerful AI tools designed to simplify and elevate
            your image editing experience. Whether you're creating, resizing, or
            cleaning up images, these features help you save time and achieve
            professional results with ease.
          </p>
        </div>
        {data.map((e, i) => (
          <div className="contentBox" key={e.id}>
            <img src={e.image} alt="" />
            <div className="content">
              <div className="icon">
                {i === 0 ? (
                  <Stars />
                ) : i === 1 ? (
                  <BGX />
                ) : i === 2 ? (
                  <Rescale />
                ) : (
                  <Stars />
                )}
              </div>
              <h2>{e.title}</h2>
              <p>{e.description}</p>
              <ul>
                {e.points.map((e, i) => (
                  <li key={i}>
                    {i === 0 ? (
                      <BrushAndPen />
                    ) : i === 1 ? (
                      <EditIcon />
                    ) : i === 2 ? (
                      <CreateIcon />
                    ) : (
                      <BrushAndPen />
                    )}
                    {e}
                  </li>
                ))}
              </ul>
            </div>
          </div>
        ))}
      </div>
    </div>
  );
}

export default NewFeatures;

